DROP TABLE IF EXISTS users CASCADE;
DROP TABLE IF EXISTS items CASCADE;
CREATE TABLE IF NOT EXISTS users (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, name varchar(100), email varchar(100) UNIQUE, registration_date TIMESTAMP WITHOUT TIME ZONE);
CREATE TABLE IF NOT EXISTS items (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, name varchar(100), description varchar(300), available boolean, owner_id BIGINT, request_id BIGINT, CONSTRAINT fk_items_to_users FOREIGN KEY(owner_id) REFERENCES users(id) ON DELETE CASCADE, CONSTRAINT fk_items_to_requests FOREIGN KEY(request_id) REFERENCES requests(id) ON DELETE CASCADE );
CREATE TABLE IF NOT EXISTS bookings (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, start_date TIMESTAMP WITHOUT TIME ZONE, end_date TIMESTAMP WITHOUT TIME ZONE, item_id BIGINT, booker_id BIGINT, status varchar(100), CONSTRAINT fk_bookings_to_items FOREIGN KEY(item_id) REFERENCES items(id) ON DELETE CASCADE, CONSTRAINT fk_bookings_to_users FOREIGN KEY(booker_id) REFERENCES users(id) ON DELETE CASCADE );
CREATE TABLE IF NOT EXISTS requests (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, description varchar(300), requestor_id BIGINT, CONSTRAINT fk_requests_to_users FOREIGN KEY(requestor_id) REFERENCES users(id) ON DELETE CASCADE );